
public class ReinvierteEnBanco {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=0;
		int montoini=700;
		while (montoini < 1500) {
			montoini=montoini+14;
			i++;
		}
		System.out.println("Tuvieron que pasar "+i+" mes para tener un monto mayor a 1500");
	
	}

}
